Movie.create(title: "Angels & Demons", director: "Ron Howard", description: "Harvard symbologist Robert Langdon works with a nuclear physicist to solve a murder and prevent a terrorist act against the Vatican during one of the significant events within the church.", image: "https://m.media-amazon.com/images/M/MV5BMjEzNzM2MjgxMF5BMl5BanBnXkFtZTcwNTQ1MTM0Mg@@._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 2009)
Movie.create(title: "You've Got Mail", director: "Nora Ephron", description: "Two business rivals who despise each other in real life unwittingly fall in love over the Internet.", image: "https://m.media-amazon.com/images/M/MV5BZTcxNzgzZjMtYzZiZC00MmE1LTg3MzQtZDAxMTYyZWE4MDNhL2ltYWdlXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 1998)
Movie.create(title: "Captain Phillips", director: "Paul Greengrass", description: "The true story of Captain Richard Phillips and the 2009 hijacking by Somali pirates of the U.S.-flagged MV Maersk Alabama, the first American cargo ship to be hijacked in two hundred years.", image: "https://m.media-amazon.com/images/M/MV5BODhiZWRhMjctNDUyMS00NmUwLTgwYmItMjJhOWNkZWQ3ZTQxXkEyXkFqcGdeQXVyMTMxODk2OTU@._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 2013)
Movie.create(title: "Sully", director: "Clint Eastwood", description: "The story of Chesley 'Sully' Sullenberger (Tom Hanks), an American pilot who became a hero after landing his damaged plane on the Hudson River in order to save the flight's passengers and crew.", image: "https://m.media-amazon.com/images/M/MV5BY2NmZDAwM2QtZmFiMS00OTJlLTgxMTItZDMyZmVhYjE1MDY3XkEyXkFqcGdeQXVyMTMxODk2OTU@._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 2016)
Movie.create(title: "Big", director: "Penny Marshall", description: "After wishing to be made big, a teenage boy wakes the next morning to find himself mysteriously in the body of an adult.", image: "https://m.media-amazon.com/images/M/MV5BMDQ1ODM5MTMtMjAwYi00ZGUxLTliNTMtN2ZhODAwMjVhMTRlXkEyXkFqcGdeQXVyNTAyODkwOQ@@._V1_UY268_CR0,0,182,268_AL_.jpg", release_year: 1988)
Movie.create(title: "Bachelor Party", director: "Neal Israel", description: "A soon-to-be-married man's friends throw him the ultimate bachelor party.", image: "https://m.media-amazon.com/images/M/MV5BYjgxOWYxMGQtMTc4MC00MzdmLWI0NTgtYzc1NzFiMGU4MWRjXkEyXkFqcGdeQXVyMzYxMzc3OTM@._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 1984)
Movie.create(title: "The Man with One Red Shoe", director: "Stan Dragoti", description: "A man picked randomly out of a crowd is made the target of CIA survelliance and pursuit.", image: "https://m.media-amazon.com/images/M/MV5BMTJjMWQyODItYjUwNC00ZmVhLWFjOGYtMmI3MTRjYjRjY2U0XkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_UY268_CR1,0,182,268_AL_.jpg", release_year: 1985)
Movie.create(title: "The 'Burbs", director: "Joe Dante", description: "An overstressed suburbanite and his fellow neighbors are convinced that the new family on the block are part of a murderous Satanic cult.", image: "https://m.media-amazon.com/images/M/MV5BNWE1OGExYzQtYzRjOS00MmJhLWE3OTYtZjkzOTNlMjJlZTQ4L2ltYWdlL2ltYWdlXkEyXkFqcGdeQXVyODY0NzcxNw@@._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 1989)
Movie.create(title: "Dragnet", director: "Tom Mankiewicz", description: "The equally-straight-laced and 'by the book' nephew of Joe Friday must work with his more laid-back partner to solve a mystery.", image: "https://m.media-amazon.com/images/M/MV5BZDIzNTc5YzEtZGFhNS00MDk1LTg2OGUtMWE2NzllMDg1NzJhXkEyXkFqcGdeQXVyMjY3MjUzNDk@._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 1987)
Movie.create(title: "A League of Their Own", director: "Penny Marshall", description: "Two sisters join the first female professional baseball league and struggle to help it succeed amidst their own growing rivalry.", image: "https://m.media-amazon.com/images/M/MV5BNjQ1NTQyYjktZDc5My00NDA1LWI1NmItY2ViNjQ0NDk4NmRjXkEyXkFqcGdeQXVyNTAyODkwOQ@@._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 1992)
Movie.create(title: "That Thing You Do!", director: "Tom Hanks", description: "A local Pennsylvania band scores a one hit wonder in 1964 and rides the star-making machinery as long as they can, with lots of help from its manager.", image: "https://m.media-amazon.com/images/M/MV5BOWVmN2ZhZjgtZGEzMy00NDkxLWI5YWQtYTE2ZTk0YzIyMzc0XkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 1996)
Movie.create(title: "Catch Me If You Can", director: "Steven Spielberg", description: "A seasoned FBI agent pursues Frank Abagnale Jr. who, before his 19th birthday, successfully forged millions of dollars' worth of checks while posing as a Pan Am pilot, a doctor, and a legal prosecutor.", image: "https://m.media-amazon.com/images/M/MV5BMTY5MzYzNjc5NV5BMl5BanBnXkFtZTYwNTUyNTc2._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 2002)
Movie.create(title: "The Ladykillers", director: "Ethan Coen, Joel Coen", description: "An eccentric, if not charming Southern professor and his crew pose as a classical ensemble in order to rob a casino, all under the nose of his unsuspecting but sharp old landlady.", image: "https://m.media-amazon.com/images/M/MV5BMTYwNjUxMTYzM15BMl5BanBnXkFtZTYwNjgwMjY3._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 2004)
Movie.create(title: "A Beautiful Day in the Neighborhood", director: "Marielle Helle", description: "Based on the true story of a real-life friendship between Fred Rogers and journalist Lloyd Vogel.", image: "https://m.media-amazon.com/images/M/MV5BZWE3ZDYwNmItOWM5ZS00Y2Q4LTlhNmYtODlhY2QxYzA1MDlhXkEyXkFqcGdeQXVyNjA5MDIyMzU@._V1_UX182_CR0,0,182,268_AL_.jpg", release_year: 2019)
